import{_ as l,a as n,b as s,c as r}from"./chunks/qt_project_installer.L-dmXOxq.js";import{_ as d,o as u,c,k as e,a as t,t as i,R as a}from"./chunks/framework.O0REyYVi.js";const $=JSON.parse('{"title":"Getting Started with Source and Builds","description":"","frontmatter":{"qt_version":"5.15.2"},"headers":[],"relativePath":"en/qgc-dev-guide/getting_started/index.md","filePath":"en/qgc-dev-guide/getting_started/index.md"}'),p={name:"en/qgc-dev-guide/getting_started/index.md"},h=a('<h1 id="getting-started-with-source-and-builds" tabindex="-1">Getting Started with Source and Builds <a class="header-anchor" href="#getting-started-with-source-and-builds" aria-label="Permalink to &quot;Getting Started with Source and Builds&quot;">​</a></h1><p>This topic explains how to get the <em>QGroundControl</em> source code and build it either natively or within a <em>Vagrant</em> environment. It also provides information about optional or OS specific functionality.</p><h2 id="daily-builds" tabindex="-1">Daily Builds <a class="header-anchor" href="#daily-builds" aria-label="Permalink to &quot;Daily Builds&quot;">​</a></h2><p>If you just want to test (and not debug) a recent build of <em>QGroundControl</em> you can use the <a href="https://docs.qgroundcontrol.com/en/releases/daily_builds.html" target="_blank" rel="noreferrer">Daily Build</a>. Versions are provided for all platforms.</p><h2 id="source-code" tabindex="-1">Source Code <a class="header-anchor" href="#source-code" aria-label="Permalink to &quot;Source Code&quot;">​</a></h2><p>Source code for <em>QGroundControl</em> is kept on GitHub here: <a href="https://github.com/mavlink/qgroundcontrol" target="_blank" rel="noreferrer">https://github.com/mavlink/qgroundcontrol</a>. It is <a href="https://github.com/mavlink/qgroundcontrol/blob/master/COPYING.md" target="_blank" rel="noreferrer">dual-licensed under Apache 2.0 and GPLv3</a>.</p><p>To get the source files:</p><ol><li>Clone the repo (or your fork) including submodules:<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>git clone --recursive -j8 https://github.com/mavlink/qgroundcontrol.git</span></span></code></pre></div></li><li>Update submodules (required each time you pull new source code):<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>git submodule update --recursive</span></span></code></pre></div></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Github source-code zip files cannot be used because these do not contain the appropriate submodule source code. You must use git!</p></div><h2 id="build-qgroundcontrol" tabindex="-1">Build QGroundControl <a class="header-anchor" href="#build-qgroundcontrol" aria-label="Permalink to &quot;Build QGroundControl&quot;">​</a></h2><h3 id="using-containers" tabindex="-1">Using Containers <a class="header-anchor" href="#using-containers" aria-label="Permalink to &quot;Using Containers&quot;">​</a></h3><p>We support Linux builds using a container found on the source tree of the repository, which can help you develop and deploy the QGC apps without having to install any of the requirements on your local environment.</p><p><a href="./../getting_started/container.html">Container Guide</a></p><h3 id="native-builds" tabindex="-1">Native Builds <a class="header-anchor" href="#native-builds" aria-label="Permalink to &quot;Native Builds&quot;">​</a></h3><p><em>QGroundControl</em> builds are supported for macOS, Linux, Windows, iOS and Android. <em>QGroundControl</em> uses <a href="http://www.qt.io" target="_blank" rel="noreferrer">Qt</a> as its cross-platform support library and uses <a href="http://doc.qt.io/qtcreator/index.html" target="_blank" rel="noreferrer">QtCreator</a> as its default build environment.</p>',15),g=a('<li><p><strong>macOS:</strong> v10.11 or higher</p></li><li><p><strong>Ubuntu:</strong> 64 bit, gcc compiler</p></li><li><p><strong>Windows:</strong> Vista or higher, <a href="#vs">Visual Studio 2019 compiler</a> (64 bit)</p></li><li><p><strong>iOS:</strong> 10.0 and higher</p></li><li><p><strong>Android:</strong> Android 5.0 and later.</p><ul><li>Standard QGC is built against ndk version 19.</li><li>Java JDK 11 is required.</li></ul></li>',5),m=e("strong",null,"Qt version:",-1),b=e("strong",null,"(only)",-1),_={class:"warning custom-block"},f=e("p",{class:"custom-block-title"},"WARNING",-1),v=e("strong",null,"Do not use any other version of Qt!",-1),k=a('<p>For more information see: <a href="http://doc.qt.io/qt-5/supported-platforms.html" target="_blank" rel="noreferrer">Qt 5 supported platform list</a>.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Native <a href="./../getting_started/cent_os.html">CentOS Builds</a> are also supported, but are documented separately (as the tested environment is different).</p></div><h4 id="vs" tabindex="-1">Install Visual Studio 2019 (Windows Only) <a class="header-anchor" href="#vs" aria-label="Permalink to &quot;Install Visual Studio 2019 (Windows Only) {#vs}&quot;">​</a></h4><p>The Windows compiler can be found here: <a href="https://visualstudio.microsoft.com/vs/older-downloads/" target="_blank" rel="noreferrer">Visual Studio 2019 compiler</a> (64 bit)</p><p>When installing, select <em>Desktop development with C++</em> as shown:</p><p><img src="'+n+'" alt="Visual Studio 2019 - Select Desktop Environment with C++"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Visual Studio is ONLY used to get the compiler. Actually building <em>QGroundControl</em> should be done using <a href="#qt-creator">Qt Creator</a> or <a href="#qmake">qmake</a> as outlined below.</p></div><h4 id="install-qt" tabindex="-1">Install Qt <a class="header-anchor" href="#install-qt" aria-label="Permalink to &quot;Install Qt&quot;">​</a></h4><p>You <strong>need to install Qt as described below</strong> instead of using pre-built packages from say, a Linux distribution, because <em>QGroundControl</em> needs access to private Qt headers.</p><p>To install Qt:</p>',10),C=a('<li><p>Download and run the <a href="http://www.qt.io/download-open-source" target="_blank" rel="noreferrer">Qt Online Installer</a></p><ul><li><strong>Ubuntu:</strong><ul><li>Set the downloaded file to executable using: <code>chmod +x</code>.</li><li>Install to default location for use with <strong>./qgroundcontrol-start.sh.</strong> If you install Qt to a non-default location you will need to modify <strong>qgroundcontrol-start.sh</strong> in order to run downloaded builds.</li></ul></li></ul></li>',1),y=e("em",null,"Select Components",-1),q=a('<div class="info custom-block"><p class="custom-block-title">INFO</p><p>If the version needed is not displayed, check the archive (show archive and refresh).</p></div><p>Then install just the following components:</p><ul><li><strong>Windows</strong>: <em>MSVC 2019 64 bit</em></li><li><strong>MacOS</strong>: <em>macOS</em></li><li><strong>Linux</strong>: <em>Desktop gcc 64-bit</em></li><li>All: <ul><li><em>Qt Charts</em></li><li><em>Android ARMv7</em> (optional, used to build Android)</li></ul></li></ul><p><img src="'+s+'" alt="QtCreator Select Components (Windows)"></p>',4),S=a('<li><p>Install Additional Packages (Platform Specific)</p><ul><li><p><strong>Ubuntu:</strong> <code>sudo apt-get install speech-dispatcher libudev-dev libsdl2-dev patchelf build-essential curl</code></p></li><li><p><strong>Fedora:</strong> <code>sudo dnf install speech-dispatcher SDL2-devel SDL2 systemd-devel patchelf</code></p></li><li><p><strong>Arch Linux:</strong> <code>pacman -Sy speech-dispatcher patchelf</code></p></li><li><p><strong>Android:</strong> <a href="http://doc.qt.io/qt-5/androidgs.html" target="_blank" rel="noreferrer">Qt Android Setup</a></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>JDK11 is required (install if needed)!</p></div></li></ul></li><li><p>Install Optional/OS-Specific Functionality</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Optional features that are dependent on the operating system and user-installed libraries are linked/described below. These features can be forcibly enabled/disabled by specifying additional values to qmake.</p></div><ul><li><strong>Video Streaming/Gstreamer:</strong> - see <a href="https://github.com/mavlink/qgroundcontrol/blob/master/src/VideoReceiver/README.md" target="_blank" rel="noreferrer">Video Streaming</a>.</li><li><strong>Airmap SDK:</strong> - TBD.</li></ul></li><li><p>Disable platform-specific optional features that are enabled (but not installed), by default.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>This currently applies to Airmap on Linux, which is optional but enabled by default.</p></div><ul><li><strong>Ubuntu:</strong><ul><li>Airmap: Create a file named <strong>user_config.pri</strong> (in the repo root directory) containing the text <code>DEFINES += DISABLE_AIRMAP</code>. This can be done in a bash terminal using the command:<div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>echo -e &quot;DEFINES += DISABLE_AIRMAP\\r\\n&quot; | tee user_config.pri</span></span></code></pre></div></li></ul></li></ul></li>',3),w=e("h4",{id:"qt-creator",tabindex:"-1"},[t("Building using Qt Creator "),e("a",{class:"header-anchor",href:"#qt-creator","aria-label":'Permalink to "Building using Qt Creator {#qt-creator}"'},"​")],-1),I=e("li",null,[e("p",null,[t("Launch "),e("em",null,"Qt Creator"),t(" and open the "),e("strong",null,"qgroundcontrol.pro"),t(" project.")])],-1),T=e("p",null,[t("In the "),e("strong",null,"Projects"),t(" section, select the appropriate kit for your needs:")],-1),A=e("strong",null,"OSX:",-1),Q=e("div",{class:"info custom-block"},[e("p",{class:"custom-block-title"},"INFO"),e("p",null,[t("iOS builds must be built using "),e("a",{href:"http://doc.qt.io/qt-5/ios-support.html",target:"_blank",rel:"noreferrer"},"XCode"),t(".")])],-1),P=e("strong",null,"Ubuntu:",-1),D=e("strong",null,"Windows:",-1),G=e("strong",null,"64bit",-1),N=e("strong",null,"Android:",-1),V=e("ul",null,[e("li",null,[t("JDK11 is required. You can confirm it is being used by reviewing the project setting: "),e("strong",null,[t("Projects > Manage Kits > Devices > Android (tab) > Android Settings > "),e("em",null,"JDK location")]),t(".")])],-1),B=e("li",null,[e("p",null,'Build using the "hammer" (or "play") icons:'),e("p",null,[e("img",{src:l,alt:"QtCreator Build Button"})])],-1),O=a(`<h4 id="qmake" tabindex="-1">Build using qmake on CLI <a class="header-anchor" href="#qmake" aria-label="Permalink to &quot;Build using qmake on CLI {#qmake}&quot;">​</a></h4><p>Example commands to build a default QGC and run it afterwards:</p><ol><li><p>Make sure you cloned the repository and updated the submodules before, see chapter <em>Source Code</em> above and switch into the repository folder:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>cd qgroundcontrol</span></span></code></pre></div></li><li><p>Create and enter a shadow build directory:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>mkdir build</span></span>
<span class="line"><span>cd build</span></span></code></pre></div></li><li><p>Configure the build using the qmake script in the root of the repository:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>qmake ../</span></span></code></pre></div></li><li><p>Run make to compile and link. To accelerate the process things you can use the <code>-j{number of threads}</code> parameter.</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>make -j12</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>You can also specify build time flags here. For example, you could disable airmap inclusion using the command:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>DEFINES+=DISABLE_AIRMAP make build</span></span></code></pre></div></div></li><li><p>Run the QGroundcontrol binary that was just built:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>./staging/QGroundControl</span></span></code></pre></div></li></ol><h3 id="vagrant" tabindex="-1">Vagrant <a class="header-anchor" href="#vagrant" aria-label="Permalink to &quot;Vagrant&quot;">​</a></h3><p><a href="https://www.vagrantup.com/" target="_blank" rel="noreferrer">Vagrant</a> can be used to build and run <em>QGroundControl</em> within a Linux virtual machine (the build can also be run on the host machine if it is compatible).</p><ol><li><a href="https://www.vagrantup.com/downloads.html" target="_blank" rel="noreferrer">Download</a> and <a href="https://www.vagrantup.com/docs/getting-started/" target="_blank" rel="noreferrer">Install</a> Vagrant</li><li>From the root directory of the <em>QGroundControl</em> repository run <code>vagrant up</code></li><li>To use the graphical environment run <code>vagrant reload</code></li></ol><h3 id="additional-build-notes-for-all-supported-os" tabindex="-1">Additional Build Notes for all Supported OS <a class="header-anchor" href="#additional-build-notes-for-all-supported-os" aria-label="Permalink to &quot;Additional Build Notes for all Supported OS&quot;">​</a></h3><ul><li><strong>Parallel builds:</strong> For non Windows builds, you can use the <code>-j#</code> option to run parellel builds.</li><li><strong>Location of built files:</strong> Individual build file results can be found in the <code>build_debug</code> or <code>build_release</code> directories. The built executable can be found in the <code>debug</code> or <code>release</code> directory.</li><li><strong>If you get this error when running <em>QGroundControl</em></strong>: <code>/usr/lib/x86_64-linux-gnu/libstdc++.so.6: version &#39;GLIBCXX_3.4.20&#39; not found.</code>, you need to either update to the latest <em>gcc</em>, or install the latest <em>libstdc++.6</em> using: <code>sudo apt-get install libstdc++6</code>.</li><li><strong>Unit tests:</strong> To run the <a href="./../contribute/unit_tests.html">unit tests</a>, build in <code>debug</code> mode with <code>UNITTEST_BUILD</code> definition, and then copy <code>deploy/qgroundcontrol-start.sh</code> script into the <code>debug</code> directory before running the tests.</li></ul><h2 id="building-qgc-installation-files" tabindex="-1">Building QGC Installation Files <a class="header-anchor" href="#building-qgc-installation-files" aria-label="Permalink to &quot;Building QGC Installation Files&quot;">​</a></h2><p>You can additionally create installation file(s) for <em>QGroundControl</em> as part of the normal build process.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>On Windows you will need to first install <a href="https://sourceforge.net/projects/nsis/" target="_blank" rel="noreferrer">NSIS</a>.</p></div><p>To add support for installation file creation you need to add <code>CONFIG+=installer</code> to your project file, or when you call <em>qmake</em>.</p><p>To do this in <em>Qt Creator</em>:</p><ul><li>Open <strong>Projects &gt; Build &gt; Build Steps &gt; qmake &gt; Additional arguments</strong>.</li><li>Enter <code>CONFIG+=installer</code> as shown: <img src="`+r+'" alt="Installer"></li></ul>',14);function x(o,E,F,L,R,j){return u(),c("div",null,[h,e("ul",null,[g,e("li",null,[e("p",null,[m,t(" "+i(o.$frontmatter.qt_version)+" ",1),b]),e("div",_,[f,e("p",null,[v,t(" QGC has been thoroughly tested with the specified version of Qt ("+i(o.$frontmatter.qt_version)+"). There is a significant risk that other Qt versions will inject bugs that affect stability and safety (even if QGC compiles).",1)])])])]),k,e("ol",null,[C,e("li",null,[e("p",null,[t("In the installer "),y,t(" dialog choose: "+i(o.$frontmatter.qt_version)+".",1)]),q]),S]),w,e("ol",null,[I,e("li",null,[T,e("ul",null,[e("li",null,[e("p",null,[A,t(" Desktop Qt "+i(o.$frontmatter.qt_version)+" clang 64 bit",1)]),Q]),e("li",null,[e("p",null,[P,t(" Desktop Qt "+i(o.$frontmatter.qt_version)+" GCC 64bit",1)])]),e("li",null,[e("p",null,[D,t(" Desktop Qt "+i(o.$frontmatter.qt_version)+" MSVC2019 ",1),G])]),e("li",null,[e("p",null,[N,t(" Android for armeabi-v7a (GCC 4.9, Qt "+i(o.$frontmatter.qt_version)+")",1)]),V])])]),B]),O])}const M=d(p,[["render",x]]);export{$ as __pageData,M as default};
