import{_ as a,c as s,o as t,ag as n}from"./chunks/framework.BudlMBKT.js";const h=JSON.parse('{"title":"일일 빌드","description":"","frontmatter":{},"headers":[],"relativePath":"ko/qgc-user-guide/releases/daily_builds.md","filePath":"ko/qgc-user-guide/releases/daily_builds.md"}'),l={name:"ko/qgc-user-guide/releases/daily_builds.md"};function o(i,e,r,d,p,u){return t(),s("div",null,e[0]||(e[0]=[n(`<h1 id="일일-빌드" tabindex="-1">일일 빌드 <a class="header-anchor" href="#일일-빌드" aria-label="Permalink to &quot;일일 빌드&quot;">​</a></h1><p>_QGroundControl_의 일일 빌드에는 <a href="./../releases/daily_build_new_features.html">새로운 기능</a>들을 제공합니다.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>일일 빌드는 안정적인 빌드보다 테스트가 부족합니다. Use at your own risk.</p></div><p>아래 링크에서 다운로드할 수 있습니다(<a href="./../getting_started/download_and_install.html">다운로드 및 설치</a>에 설명된 대로 설치).</p><ul><li>Windows <ul><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl-installer-AMD64.exe" target="_blank" rel="noreferrer">x86_64</a></li><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl-installer-ARM64.exe" target="_blank" rel="noreferrer">Arm_64</a></li></ul></li><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl.dmg" target="_blank" rel="noreferrer">OS X</a></li><li>Linux - (See installation instructions below) <ul><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl-x86_64.AppImage" target="_blank" rel="noreferrer">Linux x86_64</a></li><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl-aarch64.AppImage" target="_blank" rel="noreferrer">Linux aarch64</a></li></ul></li><li><a href="https://d176tv9ibo4jno.cloudfront.net/builds/master/QGroundControl.apk" target="_blank" rel="noreferrer">Android</a></li><li>iOS is currently unavailable</li></ul><h2 id="linux-installation-instructions" tabindex="-1">Linux Installation Instructions <a class="header-anchor" href="#linux-installation-instructions" aria-label="Permalink to &quot;Linux Installation Instructions&quot;">​</a></h2><ol><li>Make the AppImage executable</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>chmod +x QGroundControl-&lt;arch&gt;.AppImage</span></span></code></pre></div><ol start="2"><li>Enable serial-port access Add your user to the dialout group so you can talk to USB devices without root:</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo usermod -aG dialout &quot;$(id -un)&quot;</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">INFO</p><p>At login, your shell takes a snapshot of your user and group memberships. Because you just changed groups, you need a fresh login shell to pick up “dialout” access. Logging out and back in reloads that snapshot, so you get the new permissions.</p></div><ol start="3"><li>(Optional) Disable ModemManager On some Ubuntu-based systems, ModemManager can claim serial ports that QGC needs. If you don&#39;t use it elsewhere, mask or remove it.</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># preferred: stop and mask the service</span></span>
<span class="line"><span>sudo systemctl mask --now ModemManager.service</span></span>
<span class="line"><span></span></span>
<span class="line"><span># or, if you’d rather remove the package</span></span>
<span class="line"><span>sudo apt remove --purge modemmanager</span></span></code></pre></div><ol start="4"><li>Run QGroundControl Either double-click the AppImage in your file manager or launch it from a terminal:</li></ol><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>./QGroundControl-&lt;arch&gt;.AppImage</span></span></code></pre></div>`,15)]))}const g=a(l,[["render",o]]);export{h as __pageData,g as default};
