import{_ as o,a as e}from"./chunks/ardupilot_plane.CpeQTNHy.js";import{_ as r,c as l,o as a,ag as i}from"./chunks/framework.BudlMBKT.js";const _=JSON.parse('{"title":"ArduPilot 비행 모드 설정","description":"","frontmatter":{},"headers":[],"relativePath":"ko/qgc-user-guide/setup_view/flight_modes_ardupilot.md","filePath":"ko/qgc-user-guide/setup_view/flight_modes_ardupilot.md"}'),s={name:"ko/qgc-user-guide/setup_view/flight_modes_ardupilot.md"};function p(n,t,d,u,c,h){return a(),l("div",null,t[0]||(t[0]=[i('<h1 id="ardupilot-비행-모드-설정" tabindex="-1">ArduPilot 비행 모드 설정 <a class="header-anchor" href="#ardupilot-비행-모드-설정" aria-label="Permalink to &quot;ArduPilot 비행 모드 설정&quot;">​</a></h1><p><em>비행 모드</em> 섹션에서는 RC 송신기의 특정 스위치/스위치 위치에 의해 트리거되는 비행 모드 및 기타 작업을 설정할 수 있습니다.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>In order to set up flight modes you must already have</p><blockquote><ul><li>비행 모드를 설정하려면 비행 모드를 설정하기 위해 <a href="./../setup_view/radio.html">무전기를 구성</a>해야 합니다.</li><li><ul><li><a href="./../setup_view/FlightModes.html#transmitter-setup">RC 송신기 설정</a>(비행 모드 &amp; 송신기 설정)</li></ul></li></ul></blockquote><p>이 섹션에 액세스하려면, 상단 툴바에서 <strong>기어</strong> 아이콘(차량 설정)을 선택한 다음 사이드바에서 <strong>비행 모드</strong>를 선택하세요.</p><p><img src="'+o+'" alt="비행 모드 설정 - ArduCopter"></p><h2 id="비행-모드-설정" tabindex="-1">비행 모드 설정 <a class="header-anchor" href="#비행-모드-설정" aria-label="Permalink to &quot;비행 모드 설정&quot;">​</a></h2><p>ArduPilot에서 최대 6개의 다른 비행 모드를 송신기의 단일 채널에 할당할 수 있습니다(채널은 평면에서 선택 가능하지만 멀티콥터에서는 채널 5로 고정됨). 또한 ArduCopter를 사용하면 채널 7-12에 대한 추가 _채널 옵션_을 설정할 수 있습니다. 이를 통하여 스위치에 기능을 설정합니다(예: 카메라를 켜거나 실행으로 돌아가기).</p><p>비행 모드를 설정 방법:</p><ol><li><p>RC 송신기를 켭니다.</p></li><li><p>상단 툴바에서 <strong>기어</strong> 아이콘(차량 설정)을 선택한 다음 사이드바에서 <strong>비행 모드</strong>를 선택합니다.</p><p><img src="'+o+'" alt="비행 모드 설정 - ArduCopter"></p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>위 이미지는 ArduCopter의 비행 모드 설정 스크린샷입니다.</p></div></li></ol></div><ol start="3"><li><p>드롭다운에서 최대 6개의 비행 모드를 선택합니다.</p></li><li><p><strong>ArduCopter만 해당:</strong> 채널 7-12에 대해 추가 _채널 옵션_을 선택합니다.</p></li><li><p><strong>ArduPlane만 해당:</strong> 드롭다운에서 모드 채널을 선택합니다.</p><p><img src="'+e+'" alt="비행 모드 설정 - ArduPlane"></p></li><li><p>송신기의 각 모드 스위치를 차례로 선택하여 모드가 올바른 송신기 스위치에 매핑되는지 테스트하고, 선택한 비행 모드 활성화 여부를 확인합니다(활성 모드의 경우 _QGroundControl_에서 텍스트가 노란색으로 변경됩니다).</p></li></ol><p>모든 값은 변경시에 자동으로 저장됩니다.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The ArduCopter screenshot above shows a typical setup for a three position flight mode switch with an additional option of RTL being on a channel 7 switch. You can also setup 6 flight modes using two switches plus mixing on your transmitter. Scroll down to the center section of this <a href="http://ardupilot.org/copter/docs/common-rc-transmitter-flight-mode-configuration.html#common-rc-transmitter-flight-mode-configuration" target="_blank" rel="noreferrer">page</a> for tutorials on how to do that.</p></div><h2 id="see-also" tabindex="-1">See Also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See Also&quot;">​</a></h2><ul><li><a href="http://ardupilot.org/copter/docs/flight-modes.html" target="_blank" rel="noreferrer">ArduCopter 비행 모드</a></li><li><a href="http://ardupilot.org/plane/docs/flight-modes.html" target="_blank" rel="noreferrer">ArduPlane 비행 모드</a></li><li><a href="https://ardupilot.org/copter/docs/channel-7-and-8-options.html#channel-7-and-8-options" target="_blank" rel="noreferrer">ArduCopter &gt; 보조 기능 스위치</a> - 채널 설정에 대한 추가 정보</li></ul>',8)]))}const f=r(s,[["render",p]]);export{_ as __pageData,f as default};
