import{_ as t}from"./chunks/flight_replay_toggle.BSs74sxE.js";import{_ as l,c as a,o,ag as i}from"./chunks/framework.CUuwVmIT.js";const m=JSON.parse('{"title":"Replay Flight Data","description":"","frontmatter":{},"headers":[],"relativePath":"en/qgc-user-guide/fly_view/replay_flight_data.md","filePath":"en/qgc-user-guide/fly_view/replay_flight_data.md"}'),s={name:"en/qgc-user-guide/fly_view/replay_flight_data.md"};function r(n,e,p,c,g,d){return o(),a("div",null,e[0]||(e[0]=[i('<h1 id="replay-flight-data" tabindex="-1">Replay Flight Data <a class="header-anchor" href="#replay-flight-data" aria-label="Permalink to &quot;Replay Flight Data&quot;">â€‹</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This feature is intended primarily for <strong>autopilot developers</strong>/<strong>vehicle creators</strong>. It is only supported on desktop builds (Windows, Linux, Mac OS).</p></div><p>The <em>Replay Flight Data</em> feature allows users to replay a telemetry log, enabling review of past or problematic flights. The flight can be started, paused, stopped, restarted etc.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>QGroundControl</em> treats flight replay like an active connection. When you pause/stop playing, the ground station will report &quot;Communication Lost&quot; and wait for disconnection or for more messages.</p></div><p>To replay a flight:</p><ol><li><p>Disconnect any active connections.</p></li><li><p>Select <strong>Application Settings &gt; General &gt; Fly View</strong></p></li><li><p>Check <strong>Show Telemetry Log Replay Status Bar</strong> to toggle the flight replay bar at the bottom of the screen.</p><p><img src="'+t+'" alt="Toggle Flight Replay"></p></li><li><p>Select the <strong>Load Telemetry Log</strong> button in the bar to display a <em>file selection</em> dialog.</p><ul><li>Choose a log file to replay from the available telemetry logs.</li><li><em>QGroundControl</em> will immediately start playing the log.</li></ul></li><li><p>When a log is loaded you can use the:</p><ul><li><strong>Pause/Play</strong> button to pause and restart playing.</li><li><em>Slider</em> to drag to a new position in the log.</li><li><em>Rate</em> selector to choose the playback speed.</li></ul></li><li><p>To stop replay (i.e. to load a new file to replay), first pause the flight, and then select <strong>Disconnect</strong> (when it appears). After disconnecting, the <strong>Load Telemetry Log</strong> button will be displayed.</p></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>You can inspect the running replay in more detail using the <a href="./../analyze_view/mavlink_inspector.html">MAVLink Inspector</a>.</p></div>',7)]))}const y=l(s,[["render",r]]);export{m as __pageData,y as default};
