import{_ as l,a as i,b as t,c as a}from"./chunks/arducopter_setup_frame.Cfu6W-6T.js";import{_ as o,c as r,o as s,ag as n}from"./chunks/framework.CUuwVmIT.js";const v=JSON.parse('{"title":"QGC v4 Release Notes","description":"","frontmatter":{},"headers":[],"relativePath":"ko/qgc-user-guide/releases/release_note_stable_v4.md","filePath":"ko/qgc-user-guide/releases/release_note_stable_v4.md"}'),u={name:"ko/qgc-user-guide/releases/release_note_stable_v4.md"};function d(c,e,p,g,m,f){return s(),r("div",null,e[0]||(e[0]=[n('<h1 id="qgc-v4-release-notes" tabindex="-1">QGC v4 Release Notes <a class="header-anchor" href="#qgc-v4-release-notes" aria-label="Permalink to &quot;QGC v4 Release Notes&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Release notes are now tracked in the <a href="https://github.com/mavlink/qgroundcontrol/releases" target="_blank" rel="noreferrer">Github release page</a>. See that page for information about changes after v4.0.0.</p></div><h2 id="stable-version-4-0" tabindex="-1">Stable Version 4.0 <a class="header-anchor" href="#stable-version-4-0" aria-label="Permalink to &quot;Stable Version 4.0&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">INFO</p><p>The format for Settings in QGC had to change in this release. Which means all QGC settings will be reset to defaults.</p></div><ul><li>Settings <ul><li>Language: Allow selection of language</li><li>Optional <a href="./../settings_view/csv.html">CSV Logging</a> of telemetry data for improved accessibility.</li><li>ArduPilot <ul><li>Support configurable mavlink stream rates. Available from Settings/Mavlink page. <img src="'+l+'" alt="Stream Rates JPG"></li><li>Improved support for flashing ChibiOS firmware</li><li>Improved support for connecting to ChibiOS bootloader boards</li></ul></li></ul></li><li>Setup <ul><li>Joystick <ul><li>New joystick setup ui</li><li>Ability to configure held button to single or repeated action</li></ul></li><li>ArduPilot <ul><li>Motor Test</li><li>ArduSub <ul><li>Automatic motor direction detection</li></ul></li><li>ArduCopter <ul><li>PID Tuning support <img src="'+i+'" alt="PID Tuning JPG"></li><li>Additional Basic Tuning options <img src="'+t+'" alt="Basic Tuning JPG"></li><li>Copter/Rover - Frame setup ui <img src="'+a+'" alt="Setup Frame Copter JPG"></li></ul></li></ul></li></ul></li><li>Plan <ul><li>Create Plan from template with wizard like progression for completing full Plan.</li><li>Survey: Save commonly used settings as a Preset</li><li>Polygon editing <ul><li>New editing tools ui</li><li>Support for tracing a polygon from map locations</li></ul></li><li>ArduPilot <ul><li>Support for GeoFence and Rally Points using latest firmwares and mavlink v2</li></ul></li><li><a href="./../plan_view/pattern_presets.html">Pattern Presets</a><ul><li>Allows you to save settings for a Pattern item (Survey, Corridor Scan, ...) into a named preset. You can then use this preset over and over again as you create new Pattern.</li></ul></li></ul></li><li>Fly <ul><li>Click to ROI support</li><li>Added support for connecting to ADSB SBS server. Adds support for ADSB data from USB SDR Dongle running &#39;dump1090 --net&#39; for example.</li><li>Ability to turn on Heading to home, COG and Next Waypoint heading indicators in Compass.</li><li>Video <ul><li>Add support for h.265 video streams</li><li>Automatically add a <a href="./../fly_view/video_overlay.html">Video Overlay</a> with flight data as a subtitle for locally-recorded videos</li></ul></li><li>Vehicle type specific pre-flight checklists. Turn on from Settings.</li></ul></li><li>Analyze <ul><li>New Mavlink Inspector which includes charting support. Supported on all builds including Android and iOS.</li></ul></li><li>General <ul><li>Released Windows build are now 64 bit only</li><li>Log Replay: Ability to specify replay speed</li><li>ArduPilot <ul><li>Improved support for chibios firmwares and ArduPilot bootloader with respect to flashing and auto-connect.</li></ul></li></ul></li></ul>',5)]))}const b=o(u,[["render",d]]);export{v as __pageData,b as default};
