import{_ as l,a as e}from"./chunks/rally_points_delete.B0rZDnjj.js";import{_ as t,c as i,o,ag as r}from"./chunks/framework.v43S21bq.js";const f=JSON.parse('{"title":"계획 뷰 - 랠리 포인트","description":"","frontmatter":{},"headers":[],"relativePath":"ko/qgc-user-guide/plan_view/plan_rally_points.md","filePath":"ko/qgc-user-guide/plan_view/plan_rally_points.md"}'),n={name:"ko/qgc-user-guide/plan_view/plan_rally_points.md"};function s(p,a,_,c,d,u){return o(),i("div",null,a[0]||(a[0]=[r('<h1 id="계획-뷰-랠리-포인트" tabindex="-1">계획 뷰 - 랠리 포인트 <a class="header-anchor" href="#계획-뷰-랠리-포인트" aria-label="Permalink to &quot;계획 뷰 - 랠리 포인트&quot;">​</a></h1><p>랠리 포인트는 대체 착륙 또는 배회 장소입니다. 일반적으로 리턴/RTL 모드에서 홈 위치보다 더 안전하거나 더 편리한(예: 더 가까운) 목적지를 지정합니다.</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>랠리 포인트는 Rover 3.6 및 Copter 3.7(또는 그 이상)의 ArduPilot에서만 지원됩니다. PX4 지원은 PX4 v1.10에 계획되어 있습니다. 또한 일일 빌드 또는 Stable 3.6 버전을 사용하여야 합니다(한 번 사용 가능). 연결된 차량에서 지원하지 않는 경우 _QGroundControl_은 랠리 포인트 옵션을 표시하지 않습니다.</p></div><p><img src="'+l+'" alt="랠리 포인트"></p><h2 id="랠리-포인트-사용법" tabindex="-1">랠리 포인트 사용법 <a class="header-anchor" href="#랠리-포인트-사용법" aria-label="Permalink to &quot;랠리 포인트 사용법&quot;">​</a></h2><p>랠리 포인트를 생성하려면:</p><ol><li>계획 뷰로 이동합니다.</li><li>임무 명령 목록 위의 <em>랠리</em> 라디오 버튼을 선택합니다.</li><li>랠리 포인트를 지도에서 원하는 곳을 클릭합니다. <ul><li>각각에 대해 <strong>R</strong> 마커가 추가됩니다.</li><li>현재 활성화된 마커는 다른 색상(녹색)을 가지며 <em>집합 포인트</em> 패널을 사용하여 편집할 수 있습니다.</li></ul></li><li>지도에서 랠리 포인트를 선택하여 활성화합니다. <ul><li>지도에서 드래그하거나 패널에서 위치를 편집하여 활성 랠리 포인트를 이동합니다.</li><li><img src="'+e+'" alt="랠리 포인트 삭제"></li></ul></li></ol><h2 id="랠리-포인트-업로드" tabindex="-1">랠리 포인트 업로드 <a class="header-anchor" href="#랠리-포인트-업로드" aria-label="Permalink to &quot;랠리 포인트 업로드&quot;">​</a></h2><p>랠리 포인트를 <a href="./../plan_view/plan_view.html">계획 도구</a>의 <strong>파일</strong>을 사용하여 미션과 동일한 방식으로 업로드합니다.</p><h2 id="기타-도구" tabindex="-1">기타 도구 <a class="header-anchor" href="#기타-도구" aria-label="Permalink to &quot;기타 도구&quot;">​</a></h2><p>나머지 도구는 임무를 편집시와 동일한 기능을 제공합니다.</p>',11)]))}const g=t(n,[["render",s]]);export{f as __pageData,g as default};
