import{_ as l}from"./chunks/flight_replay_toggle.jfzZeWNj.js";import{_ as e,c as i,o,ag as a}from"./chunks/framework.v43S21bq.js";const u=JSON.parse('{"title":"重放飞行数据","description":"","frontmatter":{},"headers":[],"relativePath":"zh/qgc-user-guide/fly_view/replay_flight_data.md","filePath":"zh/qgc-user-guide/fly_view/replay_flight_data.md"}'),r={name:"zh/qgc-user-guide/fly_view/replay_flight_data.md"};function s(p,t,n,c,_,m){return o(),i("div",null,t[0]||(t[0]=[a('<h1 id="重放飞行数据" tabindex="-1">重放飞行数据 <a class="header-anchor" href="#重放飞行数据" aria-label="Permalink to &quot;重放飞行数据&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>此功能主要适用于 <strong>autopilot 开发者</strong>/<strong>载具制造者</strong>。 它仅在桌面版本上支持 (Windows, Linux, Mac OS)。</p></div><p><em>重播飞行数据</em> 的功能允许用户重新播放遥测日志，从而能够对过去的飞行或有问题的飞行进行审查。 飞行可以启动、暂停、停止、重新启动等。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p><em>QGroundControl</em> 将飞行重放视为活动连接。 当您暂停/停止播放时，地面站将报告“通信损失”并等待断开连接或更多消息。</p></div><p>重放飞行：</p><ol><li><p>断开任何活动连接.</p></li><li><p>选择 <strong>应用程序设置 &gt; 常规 &gt; 飞行视图</strong></p></li><li><p>检查 <strong>显示 Telemetry 日志重播状态栏</strong> 以切换屏幕底部的飞行重播栏。</p><p><img src="'+l+'" alt="切换飞行重播"></p></li><li><p>选择 <strong>Load Telemetry 日志</strong> 按钮来显示一个 _file 选择对话框。</p><ul><li>从可用的遥测日志中选择要重播的日志文件。</li><li><em>QGroundControl</em> 将立即开始播放日志。</li></ul></li><li><p>当加载日志时，您可以使用：</p><ul><li><strong>暂停/播放</strong> 按钮以暂停和重启播放。</li><li><em>滑块</em> 拖动到日志中的一个新位置。</li><li>选择播放速度的 <em>速度</em> 选择器。</li></ul></li><li><p>要停止重播(即加载新文件重播)，首先暂停飞行，然后选择 <strong>断开连接</strong> (当它出现时)。 断开连接后，将显示 <strong>Load Telemetry 日志</strong> 按钮。</p></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>您可以使用 <a href="./../analyze_view/mavlink_inspector.html">MAVLink 检查器</a>更详细地检查正在运行的回放。</p></div>',7)]))}const f=e(r,[["render",s]]);export{u as __pageData,f as default};
